mapper.sql:

    INNER JOIN RECIPE_TYPE_CATEGORY rtc ON r.RECIPE_ID = rtc.RECIPE_ID


    INNER JOIN RECIPE_METHOD_CATEGORY rmc ON r.RECIPE_ID = rmc.RECIPE_ID

WHERE 1=1

    AND rtc.TYPE_ID = #{typeCatId}


    AND rmc.METHOD_ID = #{methodCatId}


    AND r.NAME LIKE '%' || #{keyword} || '%'

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rn, A.* FROM ( SELECT DISTINCT r.RECIPE_ID, r.NAME, r.SERVING, r.TIME, r.INGREDIENT, r.SPICY_INGREDIENT FROM RECIPE r ORDER BY r.RECIPE_ID DESC ) A
WHERE ROWNUM <= #{endRow}
) WHERE rn > #{startRow}
